import { Component, OnInit, OnDestroy } from '@angular/core';
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-carousel-slider',
  templateUrl: './carousel-slider.component.html',
  styleUrls: ['./carousel-slider.component.css']
})

export class CarouselSliderComponent implements OnInit, OnDestroy {
  slidesContainer: HTMLElement | null = null;
  slideWidth: number = 0;
  currentSlide: number = 0;
  intervalId: any;


  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.slidesContainer = document.querySelector('.slides');
    const slideItem = document.querySelector('.slides-item') as HTMLElement;

    if (slideItem) {
      this.slideWidth = slideItem.offsetWidth;
    }

    this.startCarousel();
  }

  ngOnDestroy() {
    clearInterval(this.intervalId);
  }

  // Function to scroll to the next slide
  nextSlide() {
    this.currentSlide++;
    if (this.currentSlide >= 3) {
      this.currentSlide = 0; // Return to the first slide when the end is reached
    }

    this.slidesContainer?.scrollTo({
      left: this.currentSlide * this.slideWidth,
      behavior: 'smooth',
    });
  }

  // Function to autoplay the carousel
  startCarousel() {
    this.intervalId = setInterval(() => this.nextSlide(), 8000); // Switch slides every 8 seconds (adjust as needed)
  }

}




<section class="carousel">
  <div class="overlay">
    <div class="wrapper">
      <div class="image-text-1">
        <h1>Swap nu je stek!</h1>
        <p>
          Heb jij een of meerdere plantenstekjes over en zou je die graag willen ruilen voor een nieuw
          plantje? Meld je stekje hier aan en ruil ‘m via de app. Schrijf de Latijnse naam van je
          plant en eventuele verzorgingstips op een labeltje en neem ‘m mee naar de OBA Linnaeusstraat
          in Amsterdam.
        </p>
      </div>
    </div>
  </div>
  <div class="slides">
    <div class="slides-item slide-1" id="slide-1">
      <div class="image-container">
          <img src="assets/carousel-1.jpg" alt="image-carousel-1">
      </div>
    </div>

    <div class="slides-item slide-2" id="slide-2">
      <div class="image-container-2">
          <img src="assets/carousel-2.jpg" alt="image-carousel-2">
      </div>
    </div>

    <div class="slides-item slide-3" id="slide-3">
      <div class="image-container-3">
        <img src="assets/carousel-3.jpg" alt="image-carousel-3">
      </div>
    </div>
  </div>
</section>
